    private void bbActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bbActionPerformed
        if(bb.isSelected()){
            milo.setSelected(false);
            aqua.setSelected(false);
        }
    }//GEN-LAST:event_bbActionPerformed

    private void miloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_miloActionPerformed
        if(milo.isSelected()){
            bb.setSelected(false);
            aqua.setSelected(false);
        }
    }//GEN-LAST:event_miloActionPerformed

    private void aquaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_aquaActionPerformed
        if(aqua.isSelected()){
            milo.setSelected(false);
            bb.setSelected(false);
        }
    }//GEN-LAST:event_aquaActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        milo.setSelected(false);
        aqua.setSelected(false);
        bb.setSelected(false);
        uang.setText(" ");
    }//GEN-LAST:event_jButton2ActionPerformed

