    private void beliActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_beliActionPerformed
        double duit = 0;
        duit = Double.parseDouble(uang.getText());
        if(milo.isSelected()){
            duit -= 7000.0;
        }else if(bb.isSelected()){
            duit -= 6000;
        }else if(aqua.isSelected()){
            duit -= 3000;
        }
        
        if(duit < 0){
            JOptionPane.showMessageDialog(rootPane,"Maaf Uang Anda Tidak Cukup");
        }else{
            kembalian.setText(Double.toString(duit));
        }
    }//GEN-LAST:event_beliActionPerformed

